import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  CircularProgress,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  MenuItem,
} from '@mui/material';
import axios from 'axios';
import { toast } from 'react-toastify';

const API_URL = 'http://localhost:8000/api';

const CustomerDashboard = () => {
  const [tiffins, setTiffins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchPincode, setSearchPincode] = useState('');
  const [openOrderDialog, setOpenOrderDialog] = useState(false);
  const [selectedTiffin, setSelectedTiffin] = useState(null);
  const [orderQuantity, setOrderQuantity] = useState(1);
  const [deliveryAddress, setDeliveryAddress] = useState('');
  const [deliveryPincode, setDeliveryPincode] = useState('');
  const [userPincode, setUserPincode] = useState('');

  useEffect(() => {
    fetchUserPincode();
  }, []);

  const fetchUserPincode = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await axios.get(`${API_URL}/users/me/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUserPincode(response.data.pincode);
      fetchTiffins(response.data.pincode);
    } catch (error) {
      setError('Failed to fetch user data');
      toast.error('Failed to fetch user data');
    }
  };

  const fetchTiffins = async (pincode = '') => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${API_URL}/tiffins/`, {
        params: { pincode: pincode || userPincode },
      });
      setTiffins(response.data.results);
    } catch (error) {
      setError('Failed to fetch tiffins. Please try again later.');
      toast.error('Failed to fetch tiffins.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (searchPincode) {
      fetchTiffins(searchPincode);
    } else {
      fetchTiffins(userPincode);
    }
  };

  const handleOpenOrderDialog = (tiffin) => {
    setSelectedTiffin(tiffin);
    setOrderQuantity(1);
    // Pre-fill delivery address and pincode from user profile if available
    // For now, assume these are empty or come from some user context
    setDeliveryAddress('');
    setDeliveryPincode('');
    setOpenOrderDialog(true);
  };

  const handleCloseOrderDialog = () => {
    setOpenOrderDialog(false);
    setSelectedTiffin(null);
    setOrderQuantity(1);
    setDeliveryAddress('');
    setDeliveryPincode('');
  };

  const handlePlaceOrder = async () => {
    try {
      const token = localStorage.getItem('access_token');
      if (!token) {
        toast.error('Please log in to place an order.');
        return;
      }

      await axios.post(
        `${API_URL}/orders/`,
        {
          tiffin: selectedTiffin.id,
          quantity: orderQuantity,
          delivery_address: deliveryAddress,
          delivery_pincode: deliveryPincode,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      toast.success('Order placed successfully!');
      handleCloseOrderDialog();
      // Optionally refresh tiffins or update UI
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Failed to place order.');
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography color="error" align="center" sx={{ mt: 4 }}>
          {error}
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Available Tiffins
      </Typography>

      <Box sx={{ mb: 3, display: 'flex', gap: 2 }}>
        <TextField
          label="Search by Pincode"
          variant="outlined"
          size="small"
          value={searchPincode}
          onChange={(e) => setSearchPincode(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              handleSearch();
            }
          }}
          sx={{ flexGrow: 1 }}
        />
        <Button variant="contained" onClick={handleSearch}>
          Search
        </Button>
      </Box>

      <Grid container spacing={3}>
        {tiffins.length === 0 ? (
          <Grid item xs={12}>
            <Typography align="center" color="text.secondary">
              No tiffins found matching your criteria.
            </Typography>
          </Grid>
        ) : (
          tiffins.map((tiffin) => (
            <Grid item key={tiffin.id} xs={12} sm={6} md={4}>
              <Card>
                {tiffin.image && (
                  <CardMedia
                    component="img"
                    height="200"
                    image={tiffin.image}
                    alt={tiffin.name}
                  />
                )}
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {tiffin.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {tiffin.description}
                  </Typography>
                  <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                    ₹{tiffin.price}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Provided by: {tiffin.owner_name}
                  </Typography>
                  <Button
                    variant="contained"
                    color="primary"
                    sx={{ mt: 2 }}
                    onClick={() => handleOpenOrderDialog(tiffin)}
                  >
                    Order Now
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))
        )}
      </Grid>

      {/* Order Dialog */}
      <Dialog open={openOrderDialog} onClose={handleCloseOrderDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Order {selectedTiffin?.name}</DialogTitle>
        <DialogContent>
          <Typography variant="body1" sx={{ mb: 2 }}>
            Price: ₹{selectedTiffin?.price}
          </Typography>
          <TextField
            label="Quantity"
            type="number"
            fullWidth
            value={orderQuantity}
            onChange={(e) => setOrderQuantity(Math.max(1, parseInt(e.target.value) || 1))}
            sx={{ mb: 2 }}
            inputProps={{ min: 1 }}
          />
          <TextField
            label="Delivery Address"
            fullWidth
            multiline
            rows={3}
            value={deliveryAddress}
            onChange={(e) => setDeliveryAddress(e.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            label="Delivery Pincode"
            fullWidth
            value={deliveryPincode}
            onChange={(e) => setDeliveryPincode(e.target.value)}
            sx={{ mb: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseOrderDialog}>Cancel</Button>
          <Button onClick={handlePlaceOrder} variant="contained" color="primary">
            Place Order (Total: ₹{(selectedTiffin?.price || 0) * orderQuantity})
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default CustomerDashboard;
