import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Box,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import { Search as SearchIcon } from '@mui/icons-material';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useSelector } from 'react-redux';

const API_URL = 'http://localhost:8000/api';

const Home = () => {
  const navigate = useNavigate();
  const { isAuthenticated } = useSelector((state) => state.auth);

  const [searchCategory, setSearchCategory] = useState('');
  const [searchPincode, setSearchPincode] = useState('');
  const [searchStatus, setSearchStatus] = useState('Showing all available tiffins');

  const [tiffins, setTiffins] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [openOrderDialog, setOpenOrderDialog] = useState(false);
  const [selectedTiffin, setSelectedTiffin] = useState(null);
  const [orderQuantity, setOrderQuantity] = useState(1);
  const [deliveryAddress, setDeliveryAddress] = useState('');
  const [deliveryPincode, setDeliveryPincode] = useState('');

  useEffect(() => {
    // Fetch featured tiffins on component mount
    fetchFeaturedTiffins();
  }, []);

  const fetchFeaturedTiffins = async () => {
    try {
      const response = await axios.get(`${API_URL}/tiffins/`);
      setTiffins(response.data.results);
    } catch (error) {
      setError('Failed to fetch featured tiffins');
    }
  };

  const fetchTiffins = async (pincode = '', category = '') => {
    setLoading(true);
    setError(null);
    try {
      const params = {};
      if (pincode) params.pincode = pincode;
      if (category) params.search = category;

      const response = await axios.get(`${API_URL}/tiffins/`, { params });
      setTiffins(response.data.results);
    } catch (error) {
      setError('Failed to fetch tiffins. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    const category = searchCategory.trim();
    const pincode = searchPincode.trim();

    if (!category && !pincode) {
      setSearchStatus('Showing all available tiffins');
      fetchTiffins();
    } else if (category && !pincode) {
      setSearchStatus(`Searching for "${category}" everywhere`);
      fetchTiffins('', category);
    } else if (!category && pincode) {
      setSearchStatus(`Showing all tiffins in pincode ${pincode}`);
      fetchTiffins(pincode, '');
    } else {
      setSearchStatus(`Searching for "${category}" in pincode ${pincode}`);
      fetchTiffins(pincode, category);
    }
  };

  const handleClearSearch = () => {
    setSearchCategory('');
    setSearchPincode('');
    setSearchStatus('Showing all available tiffins');
    fetchTiffins();
  };

  const handleOpenOrderDialog = (tiffin) => {
    if (!isAuthenticated) {
      toast.info('Please log in to place an order.');
      navigate('/login');
    } else {
      setSelectedTiffin(tiffin);
      setOrderQuantity(1);
      setDeliveryAddress('');
      setDeliveryPincode('');
      setOpenOrderDialog(true);
    }
  };

  const handleCloseOrderDialog = () => {
    setOpenOrderDialog(false);
    setSelectedTiffin(null);
  };

  const handlePlaceOrder = async () => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post(
        `${API_URL}/orders/`,
        {
          tiffin: selectedTiffin.id,
          quantity: orderQuantity,
          delivery_address: deliveryAddress,
          delivery_pincode: deliveryPincode,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Order placed successfully!');
      handleCloseOrderDialog();
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Failed to place order.');
    }
  };

  return (
    <Container maxWidth="lg">
      {/* Hero Section */}
      <Box
        sx={{
          mt: 8,
          mb: 6,
          textAlign: 'center',
          backgroundColor: 'primary.light',
          borderRadius: 4,
          p: 4,
        }}
      >
        <Typography variant="h2" component="h1" gutterBottom>
          Welcome to Home Eats
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph>
          Discover delicious homemade food in your area
        </Typography>

        {/* Search Bar */}
        <Box
          component="form"
          onSubmit={handleSearch}
          sx={{
            display: 'flex',
            gap: 2,
            maxWidth: 600,
            mx: 'auto',
            mt: 4,
          }}
        >
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Enter your pincode"
            value={searchPincode}
            onChange={(e) => setSearchPincode(e.target.value)}
            sx={{ backgroundColor: 'white' }}
          />
          <Button
            type="submit"
            variant="contained"
            size="large"
            startIcon={<SearchIcon />}
            disabled={loading}
          >
            Search
          </Button>
        </Box>
      </Box>

      {/* Featured Tiffins Section */}
      <Box sx={{ mt: 6, mb: 4 }}>
        <Typography variant="h4" component="h2" gutterBottom>
          {searchPincode ? 'Available Tiffins' : 'Featured Tiffins'}
        </Typography>

        {loading ? (
          <Box display="flex" justifyContent="center" my={4}>
            <CircularProgress />
          </Box>
        ) : error ? (
          <Typography color="error" align="center">
            {error}
          </Typography>
        ) : (
          <Grid container spacing={4}>
            {tiffins.map((tiffin) => (
              <Grid item key={tiffin.id} xs={12} sm={6} md={4}>
                <Card
                  sx={{
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    cursor: 'pointer',
                    '&:hover': {
                      transform: 'translateY(-4px)',
                      transition: 'transform 0.2s ease-in-out',
                    },
                  }}
                  onClick={() => navigate(`/tiffin/${tiffin.id}`)}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={tiffin.image || 'https://via.placeholder.com/300x200'}
                    alt={tiffin.name}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="h3">
                      {tiffin.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {tiffin.description}
                    </Typography>
                    <Typography
                      variant="h6"
                      color="primary"
                      sx={{ mt: 2 }}
                    >
                      ₹{tiffin.price}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      By {tiffin.owner_name}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Box>

      {/* Order Dialog */}
      <Dialog open={openOrderDialog} onClose={handleCloseOrderDialog}>
        <DialogTitle>Place Order</DialogTitle>
        <DialogContent>
          {/* Add order form content here */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseOrderDialog}>Cancel</Button>
          <Button onClick={handlePlaceOrder}>Place Order</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Home;
