import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  CircularProgress,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControlLabel,
  Checkbox,
} from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:8000/api';

const OwnerDashboard = () => {
  const [tabValue, setTabValue] = useState(0);
  const [tiffins, setTiffins] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [editingTiffin, setEditingTiffin] = useState(null);
  const [tiffinForm, setTiffinForm] = useState({
    name: '',
    description: '',
    price: '',
    is_available: true,
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const tiffinsResponse = await axios.get(`${API_URL}/tiffins/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const ordersResponse = await axios.get(`${API_URL}/orders/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTiffins(tiffinsResponse.data.results);
      setOrders(ordersResponse.data.results);
    } catch (error) {
      setError('Failed to fetch dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('access_token');
      await axios.post(
        `${API_URL}/orders/${orderId}/update_status/`,
        { status: newStatus },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchData(); // Refresh data after status update
    } catch (error) {
      setError('Failed to update order status');
    }
  };

  const handleOpenDialog = (tiffin = null) => {
    setEditingTiffin(tiffin);
    if (tiffin) {
      setTiffinForm({
        name: tiffin.name,
        description: tiffin.description,
        price: tiffin.price,
        is_available: tiffin.is_available,
      });
    } else {
      setTiffinForm({
        name: '',
        description: '',
        price: '',
        is_available: true,
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setEditingTiffin(null);
    setTiffinForm({
      name: '',
      description: '',
      price: '',
      is_available: true,
    });
  };

  const handleTiffinFormChange = (e) => {
    const { name, value, checked, type } = e.target;
    setTiffinForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmitTiffin = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };
      if (editingTiffin) {
        await axios.put(
          `${API_URL}/tiffins/${editingTiffin.id}/`,
          tiffinForm,
          config
        );
      } else {
        await axios.post(`${API_URL}/tiffins/`, tiffinForm, config);
      }
      fetchData(); // Refresh data
      handleCloseDialog();
    } catch (error) {
      setError('Failed to save tiffin');
    }
  };

  const handleDeleteTiffin = async (tiffinId) => {
    if (window.confirm('Are you sure you want to delete this tiffin?')) {
      try {
        const token = localStorage.getItem('access_token');
        await axios.delete(`${API_URL}/tiffins/${tiffinId}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        fetchData(); // Refresh data
      } catch (error) {
        setError('Failed to delete tiffin');
      }
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography color="error" align="center" sx={{ mt: 4 }}>
          {error}
        </Typography>
      </Container>
    );
  }

  const activeOrders = orders.filter(
    (order) => !['delivered', 'cancelled'].includes(order.status)
  );
  const orderHistory = orders.filter((order) =>
    ['delivered', 'cancelled'].includes(order.status)
  );

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Owner Dashboard
      </Typography>

      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs value={tabValue} onChange={handleTabChange}>
          <Tab label="My Tiffins" />
          <Tab label="Active Orders" />
          <Tab label="Order History" />
        </Tabs>
      </Box>

      {tabValue === 0 && (
        <Box>
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog()}
            sx={{ mb: 3 }}
          >
            Add New Tiffin
          </Button>
          <Grid container spacing={3}>
            {tiffins.length === 0 ? (
              <Grid item xs={12}>
                <Typography align="center" color="text.secondary">
                  No tiffins found. Click 'Add New Tiffin' to get started!
                </Typography>
              </Grid>
            ) : (
              tiffins.map((tiffin) => (
                <Grid item key={tiffin.id} xs={12} sm={6} md={4}>
                  <Card>
                    <CardContent>
                      <Typography variant="h6" gutterBottom>
                        {tiffin.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {tiffin.description}
                      </Typography>
                      <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                        ₹{tiffin.price}
                      </Typography>
                      <Typography
                        variant="body2"
                        color={tiffin.is_available ? 'success.main' : 'error.main'}
                      >
                        {tiffin.is_available ? 'Available' : 'Not Available'}
                      </Typography>
                      <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                        <Button
                          variant="outlined"
                          size="small"
                          startIcon={<EditIcon />}
                          onClick={() => handleOpenDialog(tiffin)}
                        >
                          Edit
                        </Button>
                        <Button
                          variant="outlined"
                          size="small"
                          color="error"
                          startIcon={<DeleteIcon />}
                          onClick={() => handleDeleteTiffin(tiffin.id)}
                        >
                          Delete
                        </Button>
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
              ))
            )}
          </Grid>
        </Box>
      )}

      {tabValue === 1 && (
        <Grid container spacing={3}>
          {activeOrders.length === 0 ? (
            <Grid item xs={12}>
              <Typography align="center" color="text.secondary">
                No active orders found.
              </Typography>
            </Grid>
          ) : (
            activeOrders.map((order) => (
              <Grid item xs={12} key={order.id}>
                <Card>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Order #{order.id}
                    </Typography>
                    <Typography color="text.secondary" gutterBottom>
                      Tiffin: {order.tiffin_name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Customer: {order.customer_name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Quantity: {order.quantity}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Total Price: ₹{order.total_price}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Status: {order.status.replace('_', ' ')}
                    </Typography>
                    <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                      {order.status === 'pending' && (
                        <Button
                          variant="contained"
                          size="small"
                          onClick={() => handleStatusChange(order.id, 'confirmed')}
                        >
                          Confirm Order
                        </Button>
                      )}
                      {order.status === 'confirmed' && (
                        <Button
                          variant="contained"
                          size="small"
                          onClick={() => handleStatusChange(order.id, 'preparing')}
                        >
                          Mark as Preparing
                        </Button>
                      )}
                      {order.status === 'preparing' && (
                        <Button
                          variant="contained"
                          size="small"
                          onClick={() => handleStatusChange(order.id, 'ready')}
                        >
                          Mark as Ready
                        </Button>
                      )}
                      {(order.status === 'pending' || order.status === 'confirmed') && (
                        <Button
                          variant="outlined"
                          size="small"
                          color="error"
                          onClick={() => handleStatusChange(order.id, 'cancelled')}
                        >
                          Cancel Order
                        </Button>
                      )}
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))
          )}
        </Grid>
      )}

      {tabValue === 2 && (
        <Grid container spacing={3}>
          {orderHistory.length === 0 ? (
            <Grid item xs={12}>
              <Typography align="center" color="text.secondary">
                No order history found.
              </Typography>
            </Grid>
          ) : (
            orderHistory.map((order) => (
              <Grid item xs={12} key={order.id}>
                <Card>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Order #{order.id}
                    </Typography>
                    <Typography color="text.secondary" gutterBottom>
                      Tiffin: {order.tiffin_name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Customer: {order.customer_name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Quantity: {order.quantity}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Total Price: ₹{order.total_price}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Status: {order.status.replace('_', ' ')}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))
          )}
        </Grid>
      )}

      {/* Tiffin Add/Edit Dialog */}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>{editingTiffin ? 'Edit Tiffin' : 'Add New Tiffin'}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="name"
            label="Tiffin Name"
            type="text"
            fullWidth
            variant="standard"
            value={tiffinForm.name}
            onChange={handleTiffinFormChange}
          />
          <TextField
            margin="dense"
            name="description"
            label="Description"
            type="text"
            fullWidth
            multiline
            rows={3}
            variant="standard"
            value={tiffinForm.description}
            onChange={handleTiffinFormChange}
          />
          <TextField
            margin="dense"
            name="price"
            label="Price"
            type="number"
            fullWidth
            variant="standard"
            value={tiffinForm.price}
            onChange={handleTiffinFormChange}
          />
          <FormControlLabel
            control={
              <Checkbox
                checked={tiffinForm.is_available}
                onChange={handleTiffinFormChange}
                name="is_available"
              />
            }
            label="Available for Order"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button onClick={handleSubmitTiffin}>
            {editingTiffin ? 'Save Changes' : 'Add Tiffin'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default OwnerDashboard;
